def display_main_menu
  puts '=== Main Menu ==='
  puts 'Press P to play'
  puts 'Press Q to quit'
end

# Main menu loop
loop do
  display_main_menu
  choice = gets.chomp.downcase

  case choice
  when 'p'
    start_game
  when 'q'
    quit_game
    break
  else
    puts 'Invalid choice. Please try again.'
  end

  puts "\n"
end
